CREATE TABLE Service (
	Id int NOT NULL IDENTITY(1, 1) CONSTRAINT PK_Service PRIMARY KEY,
	-- Най-дългият код на операция засега е 121 знака:
	-- 'TechnoLogica.RegiX.IaosRegister67ZOUAdapter.APIService.IIaosRegister67ZOUAPI.GetREG35_Stages_By_Reg_Number_And_Waste_Code'.
	-- Не може да се използва varchar(1000), защото ключовете са до 900 байта.
	-- Услугите, които все още не могат да се използват през RegiX, нямат код на операция.
	RegiXOperation varchar(200),
	-- Конкретна администрация или група, например всички области, общини, дирекции по горите, здравни инспекции и др.
	AdministrationId int NOT NULL CONSTRAINT FK_Service_Administration FOREIGN KEY REFERENCES Administration (Id),
	-- Някои наименования са дълги стотици символи.
	Name nvarchar(1000) NOT NULL,
	-- Правно основание.
	LegalBasis nvarchar(max),
	-- Категории имат само услугите на областните и общинските администрации.
	Category nvarchar(100),
	-- TRUE ако е режим и FALSE ако е услуга.
	IsRegime bit NOT NULL,
	-- Лицензиране, Разрешение, Регистрация, Съгласуване, Уведомление, Удостоверение.
	RegimeType nvarchar(100),
	-- Дали произтича от правото на ЕС.
	IsEu bit NOT NULL,
	-- Дали услугата е вътрешно административна.
	IsInternal bit NOT NULL,
)

