CREATE TABLE Request (
	Id int NOT NULL IDENTITY(1, 1) CONSTRAINT PK_Request PRIMARY KEY,
	UserId nvarchar(128) CONSTRAINT FK_Request_AspNetUsers FOREIGN KEY REFERENCES AspNetUsers (Id),
	ServiceId int NOT NULL CONSTRAINT FK_Request_Service FOREIGN KEY REFERENCES Service (Id),
	Argument xml,
	CallContext xml,  -- Може да се разбие на 10 полета.
	DraftDateTime datetime2 NOT NULL CONSTRAINT CH_Request_DraftDateTime CHECK (DraftDateTime BETWEEN '2000-01-01' AND '2100-01-01'),
	StartDateTime datetime2,
	EndDateTime datetime2,
	DeleteDateTime datetime2,
	-- TODO: DeleteUserId ако се допуска админ да трие чуждо. RetryCount или detail таблица.
	Error nvarchar(max),
	CONSTRAINT CH_Request_StartDateTime CHECK (StartDateTime IS NULL OR (StartDateTime BETWEEN '2000-01-01' AND '2100-01-01' AND StartDateTime >= DraftDateTime)),
	CONSTRAINT CH_Request_EndDateTime CHECK (EndDateTime IS NULL OR (EndDateTime BETWEEN '2000-01-01' AND '2100-01-01' AND EndDateTime >= StartDateTime))
)

